#include <bits/stdc++.h>
using namespace std;
long long nn;
string s;
long long n,nm,nms,nmsl;
long long cn,cnm,cnms,cnmsl;
const int mod=1e9+7;
int main(){
    cin>>nn>>s;
    for(int i=0;i<nn;i++){
        if(s[i]=='j'){
            n++;
            cn+=i+1;
            n%=mod;
            cn%=mod;
        }else if(s[i]=='t'){
            nm+=n;
            nm%=mod;
            cnm+=(i+1)*n+cn;
            cnm%=mod;
        }else if(s[i]=='l'){
            nms+=nm;
            cnms+=(i+1)*nm+cnm;
            nms%=mod;
            cnms%=mod;
        }else if(s[i]=='o'){
            nmsl+=nms;
            cnmsl+=(i+1)*nms+cnms;
            nmsl%=mod;
            cnmsl%=mod;
        }
    }
    cout<<cnmsl%mod;
    return 0;
}
