#include<bits/stdc++.h>
using namespace std;
#define ll long long
const int maxn=1e6+5;
const ll mod=1e9+7;
ll t,m,n,c0,c1,h,ans,k;
ll a[maxn];
int main()
{
    cin>>t;
    while(t--)
    {
        cin>>n>>k;
        for(int i=1;i<=n*k;i++)
        {
            cin>>a[i];
        }
        int l=1,r=n*k;
        ans=0;
        m=ceil(n*1.0/2);
        while(l<=r)
        {
            r-=n-m;
            ans+=a[r];
            r--;
            l+=m-1;
        }
        cout<<ans<<endl;
    }
    return 0;
}
