#include<iostream>
#include<cstring>
using namespace std;
int n;//节点个数 
const int INF=1e6;
int map[101][101],dist[101],p[101],m;//map[i][j]：ij是否直接联通/ij边权。dist[i]：起点到距离。 
bool flag[101];//是否已
int u;//开始节点 
int main()
{
	int t;
	cin>>n>>m>>u>>t;
	memset(map,INF,sizeof(map));
	for(int i=1;i<=m;i++)
	{
		int A,B,C;
		cin>>A>>B>>C;
		map[A][B]=min(map[A][B],C);
		map[B][A]=min(map[B][A],C);
	}
	for(int i=1;i<=n;i++)
	{
		dist[i]=map[u][i];
	}
	dist[u]=0;
	flag[u]=true;
	for(int k=1;k<=n;k++)
	{
		int mini=0;
		dist[0]=INF;
		for(int i=1;i<=n;i++)
		{
			if(dist[mini]>dist[i]&&flag[i]==0)mini=i;
		}
		if(mini==0)
		{
			continue;
		}
//		cout<<mini<<endl;
		flag[mini]=true; 
		for(int i=1;i<=n;i++)
		{
			if(map[mini][i]!=INF&&flag[i]==0&&dist[i]>dist[mini]+map[mini][i])
			{
				dist[i]=dist[mini]+map[mini][i];
				p[i]=mini;
			}
		}
	}
//	for(int i=1;i<=n;i++)cout<<dist[i]<<" ";
	cout<<dist[t];
	return 0;
}
