//暴力出奇迹
#include<iostream>
using namespace std;
int a[100010],f[100010];
int cost(int s,int e)
{
	if(f[e]-f[s-1]<=40)return f[e+1]-f[s-1];
}
int main()
{
	int n;
	cin>>n;
	f[0]=0;
	for(int i=1;i<=n;i++)
	{
		cin>>a[i];
		f[i]=f[i-1]+a[i];
	}
	int minn=0x7ffffff;
	for(int i=1;i<=n;i++)
	{
		for(int j=i;j<=n;j++)
		{
			if(f[j]-f[i-1]>40)
			{
				minn=min(minn,f[j]-f[i-1]);
				break;
			}
		}
	}
	cout<<minn-40;
}
