#include<iostream>
#include<cstring>
#include<map>
#include<vector>
using namespace std;
map<string,vector<string>> m;
char type_fbi(string s){
    bool all_zero=true;for(int i=0;i<s.size();i++)if(s[i]!='0'){all_zero=false;break;}
    if(all_zero)return 'B';
    bool all_one=true;for(int i=0;i<s.size();i++)if(s[i]!='1'){all_one=false;break;}
    if(all_one)return 'I';
    return 'F';
}
void f(string s){
    if(s.size()>=2){
        string s1=s.substr(0,s.size()/2);
        string s2=s.substr(s.size()/2,s.size()/2);
        vector<string> tmp={s1,s2};
        m[s]=tmp;
        f(s1);f(s2);
    }
}
void dfs(string s){
    for(int i=0;i<m[s].size();i++){
        dfs(m[s][i]);
    }
    cout<<type_fbi(s);
}
int main(){
    int n;
    string str;
    cin>>n>>str;
    f(str);
    dfs(str);
    return 0;
}
