#include <iostream> 
#include <cstring>
#include <set>
using namespace std;
const int N = 10;
string s;
set<string> st;
int ans;
bool vis[N];
string change(int w,string s)
{
	string str = "";
	if(w==1) return str = s;
	if(w==2)
	{
		str+=s[3],str+=s[2],str+=s[0];
		str+=s[1],str+=s[4],str+=s[5];
	}
	if(w==3)
	{
		str+=s[4],str+=s[5],str+=s[2];
		str+=s[3],str+=s[1],str+=s[0];
	}
	if(w==4)
	{
		str+=s[2],str+=s[3],str+=s[1];
		str+=s[0],str+=s[4],str+=s[5];
	}
	if(w==5)
	{
		str+=s[1],str+=s[0],str+=s[3];
		str+=s[2],str+=s[4],str+=s[5];
	}
	if(w==6)
	{
		str+=s[5],str+=s[4],str+=s[3];
		str+=s[2],str+=s[1],str+=s[0];
	}
	return str;
}
string rorate(string s)
{
	string str = s.substr(0,2);
	str+=s[4];
	str+=s[5];
	str+=s[3];
	str+=s[2];
	return str;
}
void dfs(string str)
{
	if(str.size()==6)
	{
		if(!st.count(str))
		{
			ans++;
			for(int i=1;i<=6;i++)
			{
				string sp = change(i,str);
				for(int j=1;j<=4;j++) st.insert(sp = rorate(sp));
			}
		}
		return;
	}
	for(int i=0;i<s.size();i++)
	{
		if(vis[i]) continue;
		vis[i] = 1;
		dfs(str+s[i]);
		vis[i] = 0;
	}
}
int main()
{
	cin>>s;
	dfs("");
	printf("%d\n",ans);
	return 0;
}
