#include<cstdio>
#include<cstring>
#include<algorithm>
#include<iostream>
using namespace std;

const int maxn = 500000 + 5;
int vis[maxn];

void init(){
    memset(vis,0,sizeof(vis));
    for(int i = 1; i <= 500; i ++){
        int t = i * (i + 1) / 2;
        vis[t] = 1;
    }
}

int main(){
    int n;
    init();
    while(scanf("%d",&n)!=EOF){
        if(vis[n] == 1) printf("YES\n");
        else printf("NO\n");
    }
    return 0;
}
