#include<iostream>
#include<iomanip>
using namespace std;
bool Cpos[2100][2100];long long n,m,Mx,My;
long long mapCnt[2100][2100];//走法
bool isYuejie(long long x,long long  y){
    if(x>=0&&y>=0&&x<=n&&y<=m)return false;
    else return true;
}
int main(){
    for(int i=0;i<=21;i++)for(int j=0;j<=21;j++)Cpos[i][j]=true;
    for(int i=0;i<=21;i++)for(int j=0;j<=21;j++)mapCnt[i][j]=0;
    cin>>n>>m>>Mx>>My;
    if(!isYuejie(Mx-2,My-1))Cpos[Mx-2][My-1]=false;
    if(!isYuejie(Mx-2,My+1))Cpos[Mx-2][My+1]=false;
    if(!isYuejie(Mx+2,My-1))Cpos[Mx+2][My-1]=false;
    if(!isYuejie(Mx+2,My+1))Cpos[Mx+2][My+1]=false;

    if(!isYuejie(Mx-1,My-2))Cpos[Mx-1][My-2]=false;
    if(!isYuejie(Mx-1,My+2))Cpos[Mx-1][My+2]=false;
    if(!isYuejie(Mx+1,My-2))Cpos[Mx+1][My-2]=false;
    if(!isYuejie(Mx+1,My+2))Cpos[Mx+1][My+2]=false;
    if(!isYuejie(Mx,My))Cpos[Mx][My]=false;
    mapCnt[0][0]=1;
    long long temp1,temp2;
    for(int i=0;i<=n;i++){
        for(int j=0;j<=m;j++){
            if(i==0&&j==0)continue;
            if(Cpos[i][j]){
                temp1=0;temp2=0;
                if(!isYuejie(i-1,j))temp1=mapCnt[i-1][j];
                if(!isYuejie(i,j-1))temp2=mapCnt[i][j-1];
                mapCnt[i][j]=temp1+temp2;
            }else mapCnt[i][j]=0;
        }
    };cout<<mapCnt[n][m]<<endl;
    return 0;
}
