#include<iostream>
#include<cstring>
#include<climits>
using namespace std;
int main(){
    int n,m;cin>>n>>m;
    int v[m+5],w[m+5];
    memset(v,0,sizeof(v));
    memset(w,0,sizeof(w));
    int f[m+5][n+5];
    for(int i=1;i<=m;i++){
        cin>>v[i]>>w[i];
    }
    for(int i=0;i<m+5;i++)for(int j=0;j<n+5;j++)f[i][j]=-10000000;
    f[0][0]=0;
    int maxr=INT_MIN;
    for(int i=1;i<=m;i++){
        for(int j=0;j<=n;j++){
            f[i][j]=f[i-1][j];
            if(v[i]<=j)f[i][j]=max(f[i][j],f[i-1][j-v[i]]+w[i]*v[i]);
            //cout<<f[i][j]<<" ";
            maxr=max(maxr,f[m][j]);
        }
    }
    cout<<maxr;
    return 0;
}
