#include<iostream>
#include<queue>
#define maxn 500
#define maxm 20000
using namespace std;
int n,p;
int deg[maxn];
bool vis[maxn];
struct Edge{
    int v,w,nxt;// v边的中点，nxt表示跟着一条边同一个七点上一条边的位置
}e[maxm];// 大小取决于边的数量
int head[maxn],tot;// tot 表示已经加入了tot条边
// head[i] 表示最后一条加入的起点为i的边的位置（e数组的下标）
// 加入一条u到v的有向边
void add_edge(int u,int v,int w){
    tot++;e[tot].v=v;
    e[tot].w=w;
    e[tot].nxt=head[u];
    head[u]=tot;
}
int C[maxn],U[maxn];
void topsort(){
    queue<int> que;
    for(int i=1;i<=n;i++){
        cin>>C[i]>>U[i];
        if(!C[i]){
            C[i]-=U[i];
        }
        else que.push(i);
    }
    for(int i=0;i<p;i++){
        int a,b,w;
        cin>>a>>b>>w;
        add_edge(a,b,w);
        deg[b]++;
    }
    while(!que.empty()){
        int cur=que.front();
        que.pop();
        for(int i=head[cur];i;i=e[i].nxt){
            int v=e[i].v,w=e[i].w;
            if(C[cur]>0)C[v]+=w*C[cur];
            deg[v]--;
            if(!deg[v]){
                que.push(v);
            }
        }
    }
}
int main(){
    cin>>n>>p;
    topsort();
    bool flag=false;
    for(int i=1;i<=n;i++){
        if(!head[i]&&C[i]>0){
            cout<<i<<" "<<C[i]<<endl;
            flag=true;
        }
    }
    if(!flag)cout<<"NULL"<<endl;
    return 0;
}
