#include<cstdio>
#include<algorithm>
#include<climits>
#define ll long long
using namespace std;
ll a[100005];
ll f[100005];
ll f2[100005];
int main(){
    ll n=1;
    ll temp;
    ll ret=0,ret2=0;
    while(scanf("%lld",&a[n])!=EOF) n++;
    n--;
    for(ll i=1;i<=n;i++){
        f[i]=1;
        f2[i]=1;
        for(ll j=1;j<i;j++){
            if(a[j]>=a[i]){f[i]=max(f[i],f[j]+1);}
            if(a[j]<a[i]){f2[i]=max(f2[i],f2[j]+1);}
        }
        ret=max(ret,f[i]);
        ret2=max(ret2,f2[i]);
    }
    printf("%d\n%d",ret,ret2);
    return 0;
}
