#include <bits/stdc++.h>
using namespace std;
string s;
long long ans=0;
// int dp[300010];
//dp[i]表示到字符串的第i位为止能被4整除的字串的数量
bool check(int pos){
    //获取s的第pos-1位到第pos位
    string tmp = s.substr(pos-1,2);
    //将tmp转成数字
    int num = int(tmp[0]-'0')*10+int(tmp[1]-'0');
    // cout<<"num:"<<num<<endl;
    //如果num%4==0，则返回true
    if(num%4==0) return true;
    else return false;
}
int main(){
    cin>>s;
    int n=s.size();
    for(int i=0;i<n;i++){
        if(s[i]=='0'||s[i]=='4'||s[i]=='8'){
            ans++;
            // cout<<"ans1:"<<ans<<endl;
        }
    }
    for(int i=1;i<n;i++){
        if(check(i)){
            ans+=i;
            // cout<<"ans2:"<<ans<<endl;
        }
    }
    cout<<ans<<endl;
    return 0;
}
