#include <bitsdc++.h>
using namespace std;
int a, b, n, ans = -1e9;
void dfs(int day, int sum, int c, int k)
{
	if (day == n + 1)
	{
		ans = max(ans, sum);
		return;
	}
	dfs(day + 1, sum + 100 * k, 1, k);
	dfs(day + 1, sum - a, c * 2, k);
	dfs(day + 1, sum - b, 1, k + 1 * c);
}
int main()
{
	cin >> a >> b >> n;
	dfs(1, 0, 1, 1);
	cout << ans + 1000 << endl;
	return 0;
}
