#include <bits/stdc++.h>
using namespace std;
int a[100005],b[100005];
int main()
{
    string s;
    int i,j;
    cin>>s;
    int n=s.length();
    a[0]=0;
    b[n+1]=0;
    for(i=1;i<=n;i++)
    {
        if(s[i-1]>='a'&&s[i-1]<='z')
        {
            a[i]=a[i-1]+1;
        }
        else
        {
            a[i]=a[i-1];
        }
    }
    for(i=n;i>=1;i--)
    {
        if(s[i-1]>='A'&&s[i-1]<='Z')
        {
            b[i]=b[i+1]+1;
        }
        else
        {
            b[i]=b[i+1];
        }
    }
    int minx=n;
    for(i=0;i<=n;i++)
    {
        minx=min(a[i]+b[i+1],minx);
    }
    cout<<minx;
    return 0;
}
