#include <bits/stdc++.h>
using namespace std;
string s;
int lower[500005],upper[500005];
int main(){
    cin>>s;
    //计算从0~i(含)有多少个小写字母
    if(s[0]>='a'&&s[0]<='z')
        lower[0]=1;
    else
        lower[0]=0;
    for(int i=1;i<s.length();i++){
        if(s[i]>='a'&&s[i]<='z')
            lower[i]=lower[i-1]+1;
        else
            lower[i]=lower[i-1];
    }
    lower[s.length()]=lower[s.length()-1];
    //计算从n-1~i(含)有多少个大写字母
    for(int i=s.length()-1;i>=0;i--){
        if(s[i]>='A'&&s[i]<='Z')
            upper[i]=upper[i+1]+1;
        else
            upper[i]=upper[i+1];
    }
    //计算答案
    int ans=upper[0];
    for(int i=0;i<=s.length();i++){
        // cout<<lower[i]<<" "<<upper[i]<<endl;
        ans=min(ans,lower[i]+upper[i+1]);
    }
    cout<<ans;
    return 0;
}
