//input five numbers and then sort them
#include <bits/stdc++.h>
using namespace std;

int main(){
    int a,b,c,d,e;
    cin>>a>>b>>c>>d>>e;
    int arr[5]={a,b,c,d,e};
    sort(arr,arr+5);
    cout<<arr[0]<<" "<<arr[1]<<" "<<arr[2]<<" "<<arr[3]<<" "<<arr[4]<<endl;
    
    return 0;
}
